mapper.select:
WITH RECURSIVE DateRange AS (SELECT CAST(#{startTime} AS DATE) AS date UNION ALL SELECT date + INTERVAL 1 DAY FROM DateRange WHERE date < #{endTime} ) SELECT dr.date AS name, COUNT(poi.order_profit) AS value FROM DateRange dr LEFT JOIN tb_purchase_order_info poi ON dr.date = DATE (poi.purchase_time) and poi.order_type = #{orderType} and poi.dept_id in
#{deptId}
GROUP BY dr.date ORDER BY dr.date;
